	[602010302] = { talk_id = 602010302, group_id = 6020103, type = 1, next_id = 602010303, talk_content = "30%的一等奖中奖率确实是假的，中奖人选也是提前内定好的……我们就是想赚点小钱而已。", talk_name = "陶贞",},
	[602010303] = { talk_id = 602010303, group_id = 6020103, type = 2, options = {{"下次别再耍花招了。",602010304,},{"能保证不再犯吗？",602010305,},}, talk_content = "这……这是一等奖的奖品，您拿好了，我们算和解行不行？", talk_name = "陶贞",},
	[602010304] = { talk_id = 602010304, group_id = 6020103, type = 3, talk_content = "我们不是什么坏人，就是一时财迷心窍。放过我们吧，以后一定好好做人！", talk_name = "陶贞",},
