	[602070303] = { talk_id = 602070303, group_id = 6020703, type = 4, next_id = 602070304, aside = "<color=#FFEBA2>播报员：</color>1号车手是我们连续蝉联三届的冠军，实力深不可测绰号“车神”的张拓海！", talk_content = "1号车手是我们连续蝉联三届的冠军，实力深不可测绰号“车神”的张拓海！", talk_name = "播报员",},
	[602070304] = { talk_id = 602070304, group_id = 6020703, type = 4, next_id = 602070305, aside = "<color=#FFEBA2>播报员：</color>2号车手是擅长直道加速，曾打破过天月城赛道记录的梁介！", talk_content = "2号车手是擅长直道加速，曾打破过天月城赛道记录的梁介！", talk_name = "播报员",},
	[602070305] = { talk_id = 602070305, group_id = 6020703, type = 4, next_id = 602070306, aside = "<color=#FFEBA2>播报员：</color>3号车手是屡败屡战，曾留下“不胜传说”但是仍活跃在各类赛事的车手李毅！", talk_content = "3号车手是屡败屡战，曾留下“不胜传说”但是仍活跃在各类赛事的车手李毅！", talk_name = "播报员",},
	[602070306] = { talk_id = 602070306, group_id = 6020703, type = 4, next_id = 602070307, aside = "<color=#FFEBA2>播报员：</color>还要特别介绍一位新加入我们比赛的车手，身份神秘的脉者<nike-name>!", talk_content = "还要特别介绍一位新加入我们比赛的车手，身份神秘的脉者<nike-name>!", talk_name = "播报员",},
	[602070307] = { talk_id = 602070307, group_id = 6020703, type = 4, aside = "<color=#FFEBA2>播报员：</color>真是令人期待啊，究竟谁能摘下今天的冠军桂冠呢？让我们拭目以待吧！", talk_content = "真是令人期待啊，究竟谁能摘下今天的冠军桂冠呢？让我们拭目以待吧！", talk_name = "播报员",},
	[602070401] = { talk_id = 602070401, group_id = 6020704, type = 4, next_id = 602070402, aside = "<color=#FFEBA2>播报员：</color>哎呀，我们的新参赛车手差点就能夺得冠军了。", talk_content = "哎呀，我们的新参赛车手差点就能夺得冠军了。", talk_name = "播报员",},
	[602070402] = { talk_id = 602070402, group_id = 6020704, type = 4, aside = "<color=#FFEBA2>播报员：</color>真令人感到遗憾呐，希望下次能再接再厉吧。", talk_content = "真令人感到遗憾呐，希望下次能再接再厉吧。", talk_name = "播报员",},
	[602070501] = { talk_id = 602070501, group_id = 6020705, type = 4, next_id = 602070502, aside = "<color=#FFEBA2>播报员：</color>不敢相信，<nick_name>居然第一个冲过了终点！", talk_content = "不敢相信，<nick_name>居然第一个冲过了终点！", talk_name = "播报员",},
	[602070502] = { talk_id = 602070502, group_id = 6020705, type = 4, next_id = 602070503, aside = "<color=#FFEBA2>播报员：</color>难以置信呐朋友们，今天脱颖而出的竟然是初次参加比赛的新选手。", talk_content = "难以置信呐朋友们，今天脱颖而出的竟然是初次参加比赛的新选手。", talk_name = "播报员",},
	[602070503] = { talk_id = 602070503, group_id = 6020705, type = 4, aside = "<color=#FFEBA2>播报员：</color>人外有人，天外有天啊。让我们恭喜这位车手获得了今天的冠军奖金！", talk_content = "人外有人，天外有天啊。让我们恭喜这位车手获得了今天的冠军奖金！", talk_name = "播报员",},
	[602070601] = { talk_id = 602070601, group_id = 6020706, type = 1, talk_content = "演示镜头", talk_name = "路人",},
	[602080101] = { talk_id = 602080101, group_id = 6020801, type = 1, next_id = 602080102, not_skip = true, talk_content = "我的钱包被偷走了，可以帮帮我吗？", talk_name = "路人",},
	[602080102] = { talk_id = 602080102, group_id = 6020801, type = 2, not_skip = true, options = {{"离开",},{"询问细节",602080103,},{"<color=#fed78c>帮她找回钱包</color>",602080107,},}, talk_content = "拜托拜托了！没有证件的话我什么都做不了。", talk_name = "路人",},
	[602080103] = { talk_id = 602080103, group_id = 6020801, type = 3, next_id = 602080104, not_skip = true, talk_content = "我第一天来天月府，人生地不熟，正在找住的地方。", talk_name = "路人",},
	[602080104] = { talk_id = 602080104, group_id = 6020801, type = 3, next_id = 602080105, not_skip = true, talk_content = "刚刚因为不认识路正在查地图，突然有人狠狠撞我一下。", talk_name = "路人",},
	[602080105] = { talk_id = 602080105, group_id = 6020801, type = 3, next_id = 602080106, not_skip = true, talk_content = "回过神来就发现钱包不见了。", talk_name = "路人",},
	[602080106] = { talk_id = 602080106, group_id = 6020801, type = 3, next_id = 602080102, not_skip = true, talk_content = "唉，证件都在钱包里，这下惨了……", talk_name = "路人",},
	[602080107] = { talk_id = 602080107, group_id = 6020801, type = 3, next_id = 602080108, talk_content = "你愿意帮我找？谢谢！", talk_name = "路人",},
	[602080108] = { talk_id = 602080108, group_id = 6020801, type = 3, next_id = 602080109, not_skip = true, talk_content = "有注意到是偷你的人什么样吗？", talk_name = "十五",},
	[602080109] = { talk_id = 602080109, group_id = 6020801, type = 3, next_id = 602080110, not_skip = true, talk_content = "没有，因为我在盯着手机，\n不过印象中是三个走在一起的男人。", talk_name = "路人",},
	[602080110] = { talk_id = 602080110, group_id = 6020801, type = 3, next_id = 602080111, not_skip = true, talk_content = "你的钱包是什么样的？", talk_name = "十五",},
	[602080111] = { talk_id = 602080111, group_id = 6020801, type = 3, not_skip = true, talk_content = "一个粉红色小猪图案的女士钱包。", talk_name = "路人",},
	[602080201] = { talk_id = 602080201, group_id = 6020802, type = 1, next_id = 602080202, not_skip = true, talk_content = "啊！真的帮我找回来了！", talk_name = "路人",},
	[602080202] = { talk_id = 602080202, group_id = 6020802, type = 1, not_skip = true, talk_content = "谢谢，我请你喝奶茶吧，嘿嘿。", talk_name = "路人",},
	[602080301] = { talk_id = 602080301, group_id = 6020803, type = 1, next_id = 602080302, not_skip = true, talk_content = "啊！前面那几个男人。", talk_name = "十五",},
	[602080302] = { talk_id = 602080302, group_id = 6020803, type = 1, not_skip = true, talk_content = "<nick_name>，说不定就是他们！我们快去问问看吧。", talk_name = "十五",},
	[602080401] = { talk_id = 602080401, group_id = 6020804, type = 1, next_id = 602080402, not_skip = true, talk_content = "粉色小猪钱包，哼哼这下应该没错了。", talk_name = "十五",},
	[602080402] = { talk_id = 602080402, group_id = 6020804, type = 1, not_skip = true, talk_content = "赶快回去把它交给失主吧。", talk_name = "十五",},
	[602080501] = { talk_id = 602080501, group_id = 6020805, type = 1, next_id = 602080502, not_skip = true, talk_content = "请问你们有捡到什么东西吗？", talk_name = "十五",},
	[602080502] = { talk_id = 602080502, group_id = 6020805, type = 2, not_skip = true, options = {{"离开",},{"<color=#fed78c>和十五合作套话</color>",602080503,},}, talk_content = "一边去，没空搭理你。", talk_name = "混混模样的人",},
	[602080503] = { talk_id = 602080503, group_id = 6020805, type = 3, next_id = 602080504, not_skip = true, talk_content = "我们朋友钱包丢了，真的对她很重要，\n你们有没有看见呀？", talk_name = "十五",},
	[602080504] = { talk_id = 602080504, group_id = 6020805, type = 2, not_skip = true, options = {{"你怎么知道是被偷了？",602080505,},}, talk_content = "认倒霉吧，钱包被偷了哪有那么容易找回来。", talk_name = "混混模样的人",},
	[602080505] = { talk_id = 602080505, group_id = 6020805, type = 3, next_id = 602080506, not_skip = true, talk_content = "是哦，我们又没说是被偷了，你怎么会这么清楚呢？", talk_name = "十五",},
	[602080506] = { talk_id = 602080506, group_id = 6020805, type = 3, next_id = 602080507, not_skip = true, talk_content = "不是……我，我瞎猜的，现在这么乱，被偷也很正常吧？", talk_name = "混混模样的人",},
	[602080507] = { talk_id = 602080507, group_id = 6020805, type = 3, next_id = 602080508, not_skip = true, talk_content = "谁信？瞎猜也能猜的那么准？还是说你知道些什么？", talk_name = "十五",},
	[602080508] = { talk_id = 602080508, group_id = 6020805, type = 2, not_skip = true, options = {{"<color=#fed78c>暗示十五继续套话</color>",602080509,},}, talk_content = "什么都不知道，你别乱说啊？", talk_name = "混混模样的人",},
	[602080509] = { talk_id = 602080509, group_id = 6020805, type = 3, next_id = 602080510, not_skip = true, talk_content = "其实吧，刚才发生的事，我们在远处都看得一清二楚，不过想和你商量商量。", talk_name = "十五",},
	[602080510] = { talk_id = 602080510, group_id = 6020805, type = 3, next_id = 602080511, not_skip = true, talk_content = "你看见了？要商量什么？", talk_name = "混混模样的人",},
	[602080511] = { talk_id = 602080511, group_id = 6020805, type = 3, next_id = 602080512, not_skip = true, talk_content = "其实呢，把事情闹大也不好，要不然你把钱包给我，咱们把钱分了，我们只需告诉她是在附近捡到的。", talk_name = "十五",},
	[602080512] = { talk_id = 602080512, group_id = 6020805, type = 3, next_id = 602080513, not_skip = true, talk_content = "我们可以做个人情，大家也都有得赚。", talk_name = "十五",},
	[602080513] = { talk_id = 602080513, group_id = 6020805, type = 2, not_skip = true, options = {{"我们可从没有说过是“她”男是女啊？",602080514,},}, talk_content = "当真？你该不会骗我们吧？那女孩是你什么人？让你这样插手这事。", talk_name = "混混模样的人",},
	[602080514] = { talk_id = 602080514, group_id = 6020805, type = 3, next_id = 602080515, not_skip = true, talk_content = "对啊对啊！你露馅了吧！", talk_name = "十五",},
	[602080515] = { talk_id = 602080515, group_id = 6020805, type = 3, next_id = 602080516, not_skip = true, talk_content = "可恶？！你诈我！", talk_name = "混混模样的人",},
	[602080516] = { talk_id = 602080516, group_id = 6020805, type = 3, not_skip = true, talk_content = "快快交出来吧！要不然我们可不客气了！", talk_name = "十五",},
	[602080601] = { talk_id = 602080601, group_id = 6020806, type = 2, next_id = 602080602, not_skip = true, options = {{"还敢吗？",602080602,},{"下次可没这么简单",602080602,},}, talk_content = "我们错了，钱包还给你。", talk_name = "混混模样的人",},
	[602080602] = { talk_id = 602080602, group_id = 6020806, type = 3, not_skip = true, talk_content = "不敢了，我保证再也不敢了，您就饶了我吧。", talk_name = "混混模样的人",},
	[602080701] = { talk_id = 602080701, group_id = 6020807, type = 4, aside = "<color=#FFEBA2><nick_name>：</color>走在一起的男人，附近没有呢，去前面看看。", talk_content = "走在一起的男人，附近没有呢，去前面看看。", talk_name = "<nick_name>",},
	[602090101] = { talk_id = 602090101, group_id = 6020901, type = 1, next_id = 602090102, talk_content = "别过去，前面有几只毛毛枭不知怎么发狂了，\n在四处攻击人！", talk_name = "路人",},
	[602090102] = { talk_id = 602090102, group_id = 6020901, type = 1, next_id = 602090103, talk_content = "从没想过毛毛枭也会打人！还好我跑得快，\n真是太吓人了。", talk_name = "绿衣路人",},
	[602090103] = { talk_id = 602090103, group_id = 6020901, type = 2, options = {{"离开",},{"<color=#fed78c>我去看看情况</color>",602090104,},}, talk_content = "要是有人能够平息毛毛枭的愤怒就好了。", talk_name = "路人",},
	[602090104] = { talk_id = 602090104, group_id = 6020901, type = 3, next_id = 602090105, talk_content = "那就拜托您了，我俩帮不上什么忙，\n在这继续守着提醒人吧。", talk_name = "路人",},
	[602090105] = { talk_id = 602090105, group_id = 6020901, type = 3, talk_content = "为什么要这么做，明明直接走人不就好了……", talk_name = "绿衣路人",},
	[602090201] = { talk_id = 602090201, group_id = 6020902, type = 1, next_id = 602090202, talk_content = "哼，是你偷了毛毛枭的蛋吧？才让它们这么生气。", talk_name = "十五",},
	[602090202] = { talk_id = 602090202, group_id = 6020902, type = 1, next_id = 602090203, talk_content = "哈？怪不得你被一路追着跑过来，原来是你干的？", talk_name = "路人",},
	[602090203] = { talk_id = 602090203, group_id = 6020902, type = 1, next_id = 602090204, talk_content = "你瞎讲什么呢？我才不是这样的人。", talk_name = "绿衣路人",},
	[602090204] = { talk_id = 602090204, group_id = 6020902, type = 1, next_id = 602090205, talk_content = "还想骗人！你要是不说实话，那我可要抓你去对质了。", talk_name = "十五",},
	[602090205] = { talk_id = 602090205, group_id = 6020902, type = 2, options = {{"果然是你偷拿了对吧？",602090206,},}, talk_content = "别呀，有事好好商量不成吗？", talk_name = "绿衣路人",},
	[602090206] = { talk_id = 602090206, group_id = 6020902, type = 3, next_id = 602090207, talk_content = "我，我只是听说毛毛枭的蛋好像挺值钱的，\n就动了歪脑筋。", talk_name = "绿衣路人",},
	[602090207] = { talk_id = 602090207, group_id = 6020902, type = 3, next_id = 602090208, talk_content = "哪知道毛毛枭看着傻乎乎的，居然这么暴躁。", talk_name = "绿衣路人",},
	[602090208] = { talk_id = 602090208, group_id = 6020902, type = 3, talk_content = "好吧，真是抱歉，你拿去还给他们吧。", talk_name = "绿衣路人",},
	[602090301] = { talk_id = 602090301, group_id = 6020903, type = 1, next_id = 602090302, talk_content = "不好，毛毛枭看上去好像真的很生气。", talk_name = "十五",},
	[602090302] = { talk_id = 602090302, group_id = 6020903, type = 1, next_id = 602090303, talk_content = "要不，我们先试着交涉一下吧。", talk_name = "十五",},
	[602090303] = { talk_id = 602090303, group_id = 6020903, type = 1, next_id = 602090304, talk_content = "坏人——咕咕——坏人——", talk_name = "毛毛枭",},
	[602090304] = { talk_id = 602090304, group_id = 6020903, type = 1, talk_content = "毛毛枭不分青红皂白向你攻击了过来！",},
	[602090401] = { talk_id = 602090401, group_id = 6020904, type = 1, next_id = 602090402, talk_content = "大家都冷静一下，就不能好好说话吗？", talk_name = "十五",},
	[602090402] = { talk_id = 602090402, group_id = 6020904, type = 1, next_id = 602090403, talk_content = "咕……", talk_name = "毛毛枭",},
	[602090403] = { talk_id = 602090403, group_id = 6020904, type = 1, next_id = 602090404, talk_content = "蛋，不见了，咕……", talk_name = "毛毛枭",},
	[602090404] = { talk_id = 602090404, group_id = 6020904, type = 1, next_id = 602090405, talk_content = "咦？毛毛枭，你的蛋不见了吗？", talk_name = "十五",},
	[602090405] = { talk_id = 602090405, group_id = 6020904, type = 1, next_id = 602090406, talk_content = "绿色的家伙，偷蛋，咕咕……", talk_name = "毛毛枭",},
	[602090406] = { talk_id = 602090406, group_id = 6020904, type = 1, next_id = 602090407, options = {{"我们去确认一下就知道了",602090407,},}, talk_content = "绿色家伙？<nick_name>，会不会是那谁……", talk_name = "十五",},
	[602090407] = { talk_id = 602090407, group_id = 6020904, type = 1, talk_content = "毛毛枭，你稍等一会哦。", talk_name = "十五",},
	[602090501] = { talk_id = 602090501, group_id = 6020905, type = 1, next_id = 602090502, talk_content = "毛毛枭，你的蛋帮你要回来了。", talk_name = "十五",},
	[602090502] = { talk_id = 602090502, group_id = 6020905, type = 1, next_id = 602090503, talk_content = "咕咕！好人，咕咕！", talk_name = "毛毛枭",},
	[602090503] = { talk_id = 602090503, group_id = 6020905, type = 1, next_id = 602090504, talk_content = "下次不要被拿走了哦。", talk_name = "十五",},
	[602090504] = { talk_id = 602090504, group_id = 6020905, type = 1, talk_content = "礼物，感谢，咕咕咕！", talk_name = "毛毛枭",},
	[602100101] = { talk_id = 602100101, group_id = 6021001, type = 2, options = {{"询问孩子发生什么",602100102,},}, talk_content = "我的模型……为什么要抢我的模型……", talk_name = "小朋友",},
	[602100102] = { talk_id = 602100102, group_id = 6021001, type = 3, next_id = 602100103, talk_content = "有几个大哥哥说要借我的模型玩，我不肯给他们就抢走了。", talk_name = "小朋友",},
	[602100103] = { talk_id = 602100103, group_id = 6021001, type = 2, options = {{"离开",},{"<color=#fed78c>我帮你找回来</color>",602100104,},}, talk_content = "爸爸好不容易答应我给我买的，要是让他知道了，\n会骂我的。", talk_name = "小朋友",},
	[602100104] = { talk_id = 602100104, group_id = 6021001, type = 3, talk_content = "真的吗？那我在这里等你。", talk_name = "小朋友",},
	[602100201] = { talk_id = 602100201, group_id = 6021002, type = 1, next_id = 602100202, talk_content = "哇！真的是我的假面勇士！", talk_name = "小朋友",},
	[602100202] = { talk_id = 602100202, group_id = 6021002, type = 1, talk_content = "谢谢你帮我找回来，这下爸爸不会骂我了。", talk_name = "小朋友",},
	[602100301] = { talk_id = 602100301, group_id = 6021003, type = 2, options = {{"就是你们抢了别人东西？",602100302,},}, talk_content = "刚刚查过了，那模型是限量版的真家伙，\n咱俩这回赚了。", talk_name = "小无赖",},
	[602100302] = { talk_id = 602100302, group_id = 6021003, type = 3, talk_content = "哈？你哪位？劝你不要多管闲事。", talk_name = "小无赖",},
	[602100401] = { talk_id = 602100401, group_id = 6021004, type = 1, talk_content = "应该就是这个了，去还给孩子吧。", talk_name = "十五",},
	[602110101] = { talk_id = 602110101, group_id = 6021101, type = 2, options = {{"打听实情",602110102,},}, talk_content = "嗨，大家配合一下巡查司工作，这个方向只出不进！", talk_name = "女天月警司",},
	[602110102] = { talk_id = 602110102, group_id = 6021101, type = 3, next_id = 602110103, talk_content = "觉醒者？那对你说说也无妨。", talk_name = "女天月警司",},
	[602110103] = { talk_id = 602110103, group_id = 6021101, type = 3, next_id = 602110104, talk_content = "我们收到犯罪预警，有人在附近安放了危险的爆炸物。", talk_name = "女天月警司",},
	[602110104] = { talk_id = 602110104, group_id = 6021101, type = 3, next_id = 602110105, talk_content = "如果不小心被引爆，会有很多人受到伤害。", talk_name = "女天月警司",},
	[602110105] = { talk_id = 602110105, group_id = 6021101, type = 2, options = {{"离开",},{"<color=#fed78c>我来帮忙</color>",602110106,},}, talk_content = "最近这类案件太多，我们人手不足，你可以协助我们吗？", talk_name = "女天月警司",},
	[602110106] = { talk_id = 602110106, group_id = 6021101, type = 3, talk_content = "感谢你的协助。", talk_name = "女天月警司",},
	[602110201] = { talk_id = 602110201, group_id = 6021102, type = 1, next_id = 602110202, talk_content = "这么多爆炸物。", talk_name = "<nick_name>",},
	[602110202] = { talk_id = 602110202, group_id = 6021102, type = 1, talk_content = "先把它们的引信拆掉，待会等安保来运往安全的仓库。", talk_name = "<nick_name>",},
	[602110301] = { talk_id = 602110301, group_id = 6021103, type = 1, next_id = 602110302, talk_content = "不愧是觉醒者，多亏你及时处理。", talk_name = "女天月警司",},
	[602110302] = { talk_id = 602110302, group_id = 6021103, type = 1, talk_content = "这是奖励，感谢你的帮助。", talk_name = "女天月警司",},
	[602120101] = { talk_id = 602120101, group_id = 6021201, type = 1, next_id = 602120102, talk_content = "生物所最近开启了课题申请，我想做关于尾狐的课题研究。", talk_name = "月灵研究员",},
	[602120102] = { talk_id = 602120102, group_id = 6021201, type = 1, next_id = 602120103, talk_content = "唉，可是试过了许多尾狐都不愿意和我结契，\n是不是因为我不太讨喜啊？", talk_name = "月灵研究员",},
	[602120103] = { talk_id = 602120103, group_id = 6021201, type = 2, options = {{"离开",},{"<color=#fed78c>我来帮忙</color>",602120104,},}, talk_content = "觉醒者，你可以帮帮忙说服一些尾狐结契来协助我的研究吗？", talk_name = "月灵研究员",},
	[602120104] = { talk_id = 602120104, group_id = 6021201, type = 3, talk_content = "你愿意帮我？太感谢了！", talk_name = "月灵研究员",},
	[602120201] = { talk_id = 602120201, group_id = 6021202, type = 1, next_id = 602120202, talk_content = "这下就够了，没有你真不知道该怎么办。", talk_name = "月灵研究员",},
	[602120202] = { talk_id = 602120202, group_id = 6021202, type = 1, talk_content = "送你一点小礼物表达感谢吧。", talk_name = "月灵研究员",},
	[602130101] = { talk_id = 602130101, group_id = 6021301, type = 1, next_id = 602130102, talk_content = "最近天月府的纵火事件太过猖獗，不能放任不管。", talk_name = "天月警司",},
	[602130102] = { talk_id = 602130102, group_id = 6021301, type = 1, next_id = 602130103, talk_content = "我们收到情报，这次可以把他们一网打尽。", talk_name = "天月警司",},
	[602130103] = { talk_id = 602130103, group_id = 6021301, type = 2, options = {{"离开",},{"<color=#fed78c>我来帮忙</color>",602130104,},}, talk_content = "觉醒者，你可以出一份力协助警司，\n把他们揪出来吗？", talk_name = "天月警司",},
	[602130104] = { talk_id = 602130104, group_id = 6021301, type = 3, talk_content = "好，那我把他们可能出没位置的情报发给你，祝你好运。", talk_name = "天月警司",},
	[602130201] = { talk_id = 602130201, group_id = 6021302, type = 1, next_id = 602130202, talk_content = "多亏了你啊，要不然我们绝对不会这么快把他们都逮住。", talk_name = "天月警司",},
	[602130202] = { talk_id = 602130202, group_id = 6021302, type = 1, talk_content = "感谢你为我们城市安全付出的努力，这是你应得的。", talk_name = "天月警司",},
	[602140101] = { talk_id = 602140101, group_id = 6021401, type = 1, next_id = 602140102, talk_content = "附近有一个居民被罪犯绑架了", talk_name = "安保",},
	[602140102] = { talk_id = 602140102, group_id = 6021401, type = 1, next_id = 602140103, talk_content = "他们刚走没多远，我们出勤的人手不够，还没找到踪迹", talk_name = "安保",},
	[602140103] = { talk_id = 602140103, group_id = 6021401, type = 2, options = {{"离开",},{"<color=#fed78c>我来帮忙</color>",602140104,},}, talk_content = "可以帮助我们一下吗", talk_name = "安保",},
	[602140104] = { talk_id = 602140104, group_id = 6021401, type = 3, talk_content = "感谢你的协助", talk_name = "安保",},
	[602140201] = { talk_id = 602140201, group_id = 6021402, type = 1, next_id = 602140202, talk_content = "居民的情绪我们会妥善安顿好的", talk_name = "安保",},
	[602140202] = { talk_id = 602140202, group_id = 6021402, type = 1, talk_content = "这是你的奖励，感谢你的帮助", talk_name = "安保",},
	[602150101] = { talk_id = 602150101, group_id = 6021501, type = 1, next_id = 602150102, talk_content = "怎么，你也是月灵训练师？", talk_name = "月灵大师",},
	[602150102] = { talk_id = 602150102, group_id = 6021501, type = 2, options = {{"我考虑考虑",602150104,},{"来吧",602150103,},}, talk_content = "想要切磋一下月灵战斗吗？", talk_name = "月灵大师",},
	[602150103] = { talk_id = 602150103, group_id = 6021501, type = 3, talk_content = "那咱们就开始吧！", talk_name = "月灵大师",},
	[602150104] = { talk_id = 602150104, group_id = 6021501, type = 3, talk_content = "没问题，等你考虑好了再来。", talk_name = "月灵大师",},
	[602150201] = { talk_id = 602150201, group_id = 6021502, type = 1, next_id = 602150202, talk_content = "你的实力远超于我，确实足够努力。", talk_name = "月灵大师",},
	[602150202] = { talk_id = 602150202, group_id = 6021502, type = 1, talk_content = "我认输，还是要继续修行磨砺自己啊。", talk_name = "月灵大师",},
	[602150301] = { talk_id = 602150301, group_id = 6021503, type = 1, next_id = 602150302, talk_content = "哈哈，看来你们的配合还是不够成熟啊，\n月灵训练之路可不是闹着玩的。", talk_name = "月灵大师",},
	[602150302] = { talk_id = 602150302, group_id = 6021503, type = 1, talk_content = "怎么样，想再挑战一次吗？", talk_name = "月灵大师",},
	[602160101] = { talk_id = 602160101, group_id = 6021601, type = 1, next_id = 602160102, talk_content = "我们在夏熙路发现了一辆被盗车辆，\n应该是我们追查已久的一宗案件的嫌疑人在驾驶。", talk_name = "天月警司",},
	[602160102] = { talk_id = 602160102, group_id = 6021601, type = 1, next_id = 602160103, talk_content = "他随时都会跑掉，但如果我们出动很多警力围堵的话，\n又怕打草惊蛇。", talk_name = "天月警司",},
	[602160103] = { talk_id = 602160103, group_id = 6021601, type = 2, options = {{"离开",},{"<color=#fed78c>我来帮忙</color>",602160104,},}, talk_content = "觉醒者，你可以帮忙追上去骇入车辆拦住他吗？\n这样就不会惊动他了。", talk_name = "天月警司",},
	[602160104] = { talk_id = 602160104, group_id = 6021601, type = 3, talk_content = "记住，慢慢接近不要惊动嫌疑人！", talk_name = "天月警司",},
	[602160201] = { talk_id = 602160201, group_id = 6021602, type = 1, next_id = 602160202, talk_content = "不愧是觉醒者，这种毛头小贼对你来说轻而易举吧。", talk_name = "天月警司",},
	[602160202] = { talk_id = 602160202, group_id = 6021602, type = 1, talk_content = "我代表警司感谢你的帮助，这是你的奖励。", talk_name = "天月警司",},
	[602170101] = { talk_id = 602170101, group_id = 6021701, type = 1, next_id = 602170102, talk_content = "真苦恼啊，刚收到消息，\n有个危险的家伙混进人群里了。", talk_name = "警卫",},
	[602170102] = { talk_id = 602170102, group_id = 6021701, type = 2, options = {{"没有问题",},}, talk_content = "觉醒者，你可以用骇入帮帮忙吗？", talk_name = "警卫",},
	[602170201] = { talk_id = 602170201, group_id = 6021702, type = 1, next_id = 602170202, talk_content = "嗯...？觉醒者？难道你.......", talk_name = "可疑的人",},
	[602170202] = { talk_id = 602170202, group_id = 6021702, type = 1, talk_content = "可恶，被你发现了，那就别怪我不客气了！", talk_name = "可疑的人",},
	[602170301] = { talk_id = 602170301, group_id = 6021703, type = 1, talk_content = "多亏你才能成功找到这个家伙，真是太感谢了，\n这是给你的奖励。", talk_name = "警卫",},
	[602180101] = { talk_id = 602180101, group_id = 6021801, type = 1, next_id = 602180102, talk_content = "好想参加生物所的课题研究啊，不过可以选的月灵所剩无几了。", talk_name = "研究员",},
	[602180102] = { talk_id = 602180102, group_id = 6021801, type = 1, next_id = 602180103, talk_content = "似乎箴石之劣还没有人选？不过野生的箴石之劣很难碰见。", talk_name = "研究员",},
	[602180103] = { talk_id = 602180103, group_id = 6021801, type = 2, options = {{"离开",},{"<color=#fed78c>我来帮忙</color>",602180104,},}, talk_content = "假如你帮我找到几只愿意结契的箴石之劣，\n我课题经费分你一半怎样？", talk_name = "研究员",},
	[602180104] = { talk_id = 602180104, group_id = 6021801, type = 3, talk_content = "太好了！感谢你的协助。", talk_name = "研究员",},
	[602180201] = { talk_id = 602180201, group_id = 6021802, type = 1, next_id = 602180202, talk_content = "真厉害啊，这么难撞见的箴石之劣你都能找回来！", talk_name = "研究员",},
	[602180202] = { talk_id = 602180202, group_id = 6021802, type = 1, talk_content = "说话算话，报酬分你一半！", talk_name = "研究员",},
	[602190101] = { talk_id = 602190101, group_id = 6021901, type = 1, talk_content = "这是给你的报酬，请收下吧，谢谢你出手救我。", talk_name = "陌生人",},
	[602190201] = { talk_id = 602190201, group_id = 6021902, type = 4, aside = "<color=#FFEBA2>陌生人：</color>不……不要过来，救命！", talk_content = "不……不要过来，救命！", talk_name = "陌生人",},
	[602200101] = { talk_id = 602200101, group_id = 6022001, type = 1, talk_content = "你好啊，大悬钟的电梯坏了，我是前来维修的工人，可是我一个人根本<color=#FF0000>搬不动这个电梯</color>，该怎么完成大悬钟的维修呢，真头疼啊。", talk_name = "维修工",},
	[602200201] = { talk_id = 602200201, group_id = 6022002, type = 1, talk_content = "喔！你帮我把电梯搬回位置上了！接下来的工作就交给我吧！", talk_name = "维修工",},
	[602200301] = { talk_id = 602200301, group_id = 6022003, type = 4, aside = "<color=#FFEBA2>？？？：</color>撞死人啦，撞死人了！", talk_content = "撞死人啦，撞死人了！", talk_name = "？？？",},
	[602200401] = { talk_id = 602200401, group_id = 6022004, type = 1, next_id = 602200402, talk_content = "这人撞到我不想赔钱，唉哟疼死我了，\n各位快帮我评评理啊。", talk_name = "躺地者",},
	[602200402] = { talk_id = 602200402, group_id = 6022004, type = 1, next_id = 602200403, talk_content = "你一开口要10万我怎么赔得起啊。", talk_name = "司机",},
	[602200403] = { talk_id = 602200403, group_id = 6022004, type = 1, talk_content = "唉哟，好疼啊。", talk_name = "躺地者",},
	[602200501] = { talk_id = 602200501, group_id = 6022005, type = 1, talk_content = "这人突然就冲出马路，可我明明刹住车了。", talk_name = "司机",},
	[602200601] = { talk_id = 602200601, group_id = 6022006, type = 2, options = {{"砍他一刀吓唬下。",602200602,},{"说出短信内容",602200602,},}, talk_content = "……",},
	[602200602] = { talk_id = 602200602, group_id = 6022006, type = 1, talk_content = "你这家伙！坏我好事是吧！", talk_name = "躺地者",},
	[602200701] = { talk_id = 602200701, group_id = 6022007, type = 1, next_id = 602200702, talk_content = "原来他啥事都没，还真上当了，唉。", talk_name = "司机",},
	[602200702] = { talk_id = 602200702, group_id = 6022007, type = 1, talk_content = "谢谢你，路过的觉醒者。", talk_name = "司机",},
