	[2001901] = { id = 2001901, teach_id = 20019, close_delay_time = 1, content = "当<color=#FF0000>日相为空</color>时，受到<color=#FF0000>攻击</color>则会进入<color=#FF0000>失衡状态</color>。", img = "", res_type = 2, show_close_button = true, teach_stage = 1, topic = "失衡状态", video = "Guide_yunle.prefab",},
	[2002001] = { id = 2002001, teach_id = 20020, close_delay_time = 1, content = "敌人血条下方的<color=#dc8c00>弱点槽</color>被击破会进入<color=#dc8c00>眩晕状态</color>\n此时可以施展角色的<color=#dc8c00>五行绝技</color>。", img = "", res_type = 2, show_close_button = true, teach_stage = 1, topic = "绝技", video = "Guide_XumuJueji.prefab",},
	[2002101] = { id = 2002101, teach_id = 20021, close_delay_time = 1, content = "普攻、闪避敌人的攻击可积攒<color=#dc8c00>日相能量</color>。\n消耗<color=#dc8c00>日相能量</color>便可释放技能。", img = "", res_type = 2, show_close_button = true, teach_stage = 1, topic = "日相能量", video = "Guide_XumuSunPower.prefab",},
	[2002201] = { id = 2002201, teach_id = 20022, close_delay_time = 3, content = "携带<color=#dc8c00>离歌月灵</color>并处于脱战状态下，在敌人背后且满足隐匿攻击距离，可对敌人释放<color=#dc8c00>【隐匿打击】</color>。", img = "Textures/Icon/Single/TipsImg/ass1.png", res_type = 1, show_close_button = true, teach_stage = 1, topic = "隐匿打击", video = "",},
	[2002202] = { id = 2002202, teach_id = 20022, close_delay_time = 3, content = "敌人血条<color=#FF0000>红色部分</color>代表次隐匿打击技能的<color=#dc8c00>最大伤害</color>。", img = "Textures/Icon/Single/TipsImg/ass2.png", res_type = 1, show_close_button = true, teach_stage = 2, topic = "隐匿打击伤害", video = "",},
	[2002203] = { id = 2002203, teach_id = 20022, close_delay_time = 3, content = "白圈收缩到<color=#FF0000>红色范围内</color>点击QTE按钮可释放<color=#dc8c00>【完美隐匿打击】</color>，造成最大伤害。", img = "Textures/Icon/Single/TipsImg/ass3.png", res_type = 1, show_close_button = true, teach_stage = 3, topic = "完美隐匿打击", video = "",},
	[2002301] = { id = 2002301, teach_id = 20023, close_delay_time = 1, content = "每次释放技能或成功闪避敌人攻击可以积攒一枚<color=#FF0000>炎印</color>。\n最多可积攒三枚<color=#FF0000>炎印</color>。", img = "Textures/Icon/Single/TipsImg/FlameSign.png", res_type = 1, show_close_button = true, teach_stage = 1, topic = "炎印", video = "",},
	[2002302] = { id = 2002302, teach_id = 20023, close_delay_time = 1, content = "积攒三枚炎印便可长按普攻释放<color=#FF0000>信念技</color>。", img = "", res_type = 2, show_close_button = true, teach_stage = 2, topic = "信念技", video = "Guide_XumuCoreSkill.prefab",},
	[2002501] = { id = 2002501, teach_id = 20025, close_delay_time = 1, content = "出现<color=#FF0000>红眼预警</color>时，在攻击命中前按下<color=#dc8c00>闪避按钮</color>可触发<color=#FF0000>异质空间</color>\n成功<color=#FF0000>闪避</color> ，可获得日相能量。", img = "", res_type = 2, show_close_button = true, teach_stage = 1, topic = "闪避", video = "Guide_XumuDodge.prefab",},
