	[3000001] = { id = 3000001, teach_id = 30001, close_delay_time = 0, content = "<color=#FF0000>追加条件</color>\n· 在时间限制内击败所有敌人\n· 领域获得额外时长。", img = "Textures/Icon/Single/TipsImg/Normalattack.png", res_type = 1, show_close_button = true, teach_stage = 1, topic = "击败所有敌人", video = "",},
